import java.util.*;

public class Day06 {

    public static void main(String[] args) {
        doChallenge();
    }

    private static void doChallenge() {
        String input = getInput();
        int numberOfUniquesRequired = 14;
        List<Character> currentList = getStartingList(numberOfUniquesRequired, input);

        for (int i = numberOfUniquesRequired; i < input.length(); i++) {
            Set<Character> set = new HashSet<>(currentList);
            if (set.size() == numberOfUniquesRequired) {
                System.out.printf("First unique sequence of %d characters found at index: %d", numberOfUniquesRequired, i);
                return;
            }
            currentList.remove(0);
            currentList.add(input.charAt(i));
        }
    }
    
    private static List<Character> getStartingList(int numberOfUniquesRequired, String input) {
        List<Character> startingList = new LinkedList<>();
        for (int i = 0; i < numberOfUniquesRequired; i++) {
            startingList.add(input.charAt(i));
        }
        return startingList;
    }

    private static String getInput() {
        return "";
    }
}
